import React, { useEffect, useState } from 'react';
import { getCoin } from '../../../api/Coin';
import '../../../App.css';
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from 'react-bootstrap/Card';
import './Services.css';
import { Link } from 'react-router-dom';
import Carousel from 'react-bootstrap/Carousel'

const Services = () => {
    const [doc, setDoc] = useState({});
    useEffect(async () => {
        const temp = await getCoin();
        setDoc(temp);
    }, [])
    let Service = <div></div>;

    const objectMap = (obj, fn) =>
        Object.fromEntries(
            Object.entries(obj).map(
                ([k, v], i) => [k, fn(v, k, i)]
            )
        )

    const dp = [];

    objectMap(doc, function (c) {
        const carous = [];
        for (let i = 0; i < c.image.length; i++) {
            carous.push(
                <Carousel.Item>
                    <img
                        className="wid coin--img"
                        src={c.image[i]}
                        alt="First slide"
                    />
                </Carousel.Item>);
        }

        if (c.image.length == 1)
            dp.push(< img className="coin--img" src={c.image[0]} />);
        else
            dp.push(c.image[0] ? <Carousel fade className="size">{carous}</Carousel> : <img className="coin--img" src="data:image/jpeg;base64," />)
    })


    // for(let x=0;x<doc.length;x++) {
    //     const c=doc[x];
    //     for (let i = 0; i < c.image.length; i++) {
    //         carous.push(
    //             <Carousel.Item>
    //                 <img
    //                     className="d-block w-100 img"
    //                     src={c.image[i]}
    //                     alt="First slide"
    //                 />
    //             </Carousel.Item>);
    //     }

    //     if (c.image.length == 1)
    //         dp.push(< img className="coin--img" src={c.image[0]} />);
    //     else
    //         dp.push(c.image[0] ? <Carousel fade className="size">{carous}</Carousel> : <img className="coin--img" src="data:image/jpeg;base64," />)
    // }


    let i = 0;
    if (doc.length > 0) {
        Service =
            <div>
                <div className="search--btn">
                    <input id='search-btn' type='checkbox' />
                    <label for='search-btn'>Show search bar</label>
                    <input id='search-bar' type='text' placeholder='Search...' />
                </div>

                <div className='new coin--container'>
                    <Row xs={1} md={2} lg={3} >
                        {doc.map((coin) => (
                            <Col className='coin--item'>

                                <Card>
                                    <div className="coin--wrapper">
                                        {dp[i++]}
                                    </div>
                                    <Link to="/">
                                        <Card.Body>
                                            <Card.Title className="new--text">{coin.title}</Card.Title>
                                        </Card.Body>
                                    </Link>
                                    <Card.Footer>
                                        <small className="text-muted"><b>Published by: </b>{coin.publisher}</small>
                                    </Card.Footer>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                </div>
            </div>
    }
    return (
        <div>
            {Service}
        </div>
    )
}



export default Services;
